	constructor(){
		super();
		this.state = {
			following:''
		}
	}

	componentWillMount = () => {
		fetch('/myfollowing/' + this.props.match.params.usrid)
		.then(res => res.json())
        .then(data => this.setState({following:data}));
	}


render(){

