import React, { Component } from 'react';
import {Link} from 'react-router-dom';

class MyProfile extends Component {

  constructor(){
    super();
    this.state={
      user:'',
      grp:'',
      grpinfo:'',
      flag:'normal',
      following:'',
      followers:'',
      tag:'',
      share:'',
      msgs:'',
      post:''
    }
  }

  componentWillMount(){

    fetch('/user/' + this.props.match.params.id)
    .then(res => res.json())
    .then(data => this.setState({user:data}));
    
  }

  handleGroups = (id) => {
    this.setState({flag:'groups'});
  }


  render() {
    
    if(this.state.user)
       var user=this.state.user[0];

    if(this.state.flag=='groups' && user){
         var state = (<div>
                  <p><Link to={'/creategrp/' + user._id}><button>Create Group</button></Link></p>
                  </div>
                )
    }  

  return (
     <div>

        <div className="card text-center">
          <div className="card-header">
            <ul className="nav nav-tabs card-header-tabs">
              <li className="nav-item">
                <a className="nav-link active" href="#">Posts</a>
              </li>
              
               <li className="nav-item">
                <button className='nav-link' onClick={() => this.handleGroups(this.props.match.params.id)}>Groups</button>
               </li>
              
            </ul>
          </div>
          <div className="card-body">
          {state}
          </div>
        </div>
    </div>
 )
 
}
}

export default MyProfile;
