import React, { Component } from 'react';
import Follow from './follow';
import Followedpost from './followedpost';

class Newsfeed extends Component {

  constructor(){
    super();
    this.state={
      me:'',
      post:''
    }
  }

   componentDidMount(){

    this.setState({me:this.props.location.state.usr});
  }

   handleChange = (e) => {
    
  }
  
  handleSubmit = (e) => {
    e.preventDefault();
  }
  

  render() {
    const name = this.state.me.name;

    return (
     <div>
    
        <div className='outerbox'>
        <p style={{color:'#009688',marginLeft:10,marginTop:-1,fontWeight:'bold',fontSize:19}}>Newsfeed</p>
        <div className='innerbox'>
        <div className='innermost'>
        <p>{name}</p>
        <form onSubmit={this.handleSubmit} className='white'>
          <div className='input-field'>
            <label htmlFor='title'>Post here</label>
            <input type='text' id='title' onChange={this.handleChange} />
          </div>
          <div className='input-field'>
            <button className='btn pink lighten-1 z-dpth-0'>POST</button>
          </div>
        </form>
        </div>
        </div>
        </div>
        <br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/><br/>

        <Follow usr_id= {this.state.me._id}/>
  

        <Followedpost usr_id= {this.state.me._id} />
        
     </div>

    );
  }
}

export default Newsfeed;
