  }

  handleClick(){
      this.props.va;(true);
  }

  render() {
    
    const profile=(this.state.followed) ? (<div><Userprofile /></div>):null ;

    return (

     <div>
        <h2>Name</h2>
        <button onClick={this.handleClick.bind(this)}>Follow</button>
        {profile}
     </div>
     
    );
  }
}

export default Follow;
