import React, { Component } from 'react';

class Followedpost extends Component {
  
  constructor(props){
    super(props);
    this.state={
      user:'',
      following : '',
      posts : '',
      cmt:''
    }
  }
   
  handleClick = () => {
    var id = this.props.usr_id;
    console.log(id)
    fetch('/followedpost/' + id)
    .then(res => res.json())
    .then(data => this.setState({user:data}));

    }

  handleChange = (e) => {
    this.setState({[e.target.name]:e.target.value });
  }
  
  handlePost = (name) => {
    fetch('/showpost/' + name)
      .then(res => res.json())
      .then(data => this.setState({posts : data.posts}))
  }

  handleSubmit = (e) => {
    e.preventDeafult();

  }
 
  handleLike = (id) => {

    fetch('/post/:id')
      .then(res => res.json())
      .then(data => this.setState({posts : data.posts}))
  }

  render() { 
    var compo = (this.state.following && this.state.following.map(name => {
                  return (
                    <div><button onClick={() => this.handlePost(name)}>{name}</button></div>
                  );
                })
    )

    var posts =  (this.state.posts && this.state.posts.map(post => {
                  return (
                    <div>
                    <p>Date : {post.date}</p>
                    <p>Pic : {post.pic}</p>
                    <p>Caption : {post.caption}</p>
                    <p>Likes : {post.likes}</p>
                    //also array so map 
                    <p>Comment : {post.comment}</p>
                    <button onClick = {() => {this.handleLike(post._id)}} >Like</button>
                    <form onSubmit = {this.handleSubmit}>//with post_id add cmt
                    <input type='text' name='cmt' onChange = {this.handleChange}/>
                    <button>Comment</button>
                    </form>
                    </div>
                  );
                })
    )

  return (
  <div>
  <button onClick={this.handleClick}>show posts</button>
  {compo}
  {posts}
  </div>
  );

}}

export default Followedpost;
