import React, { Component } from 'react';

class Userprofile extends Component {

  constructor(){
    super();
    this.state={
      user:'',
      userposts:''
    }
  }

  componentWillMount(){

    fetch('/user')
    .then(res => res.json())
    .then(data => this.setState({user:data}));
    
  }

  handleClick = (id) => {
