  {
    if(user[i]._id===this.props.match.params.id)
    {
       var mainuser=(
        <div>
        <h4>Name :{user[i].name}</h4>
        <p>Email : {user[i].email}</p>
        <p>Joined On : {user[i].joindate}</p>
        <button className='btn btn-primary' onClick={() => this.handlePost(user[i]._id)}>Get Posts</button>
        <p>Following :{user[i].following}  </p>
        <p>Followers :{user[i].followers}</p>
        <button onClick={() => this.handleClick(user[i]._id)} > Follow </button>
     </div>);

    }
  }
  
    return (
     <div>
        {mainuser}
     </div>

    );
  }
}

export default Userprofile;
