import React, { Component } from 'react';

class Userprofile extends Component {

  constructor(){
    super();
    this.state={
      user:'',
      userposts:''
    }
  }

  componentWillMount(){

    fetch('/user')
    .then(res => res.json())
    .then(data => this.setState({user:data}));
    
  }

  handleClick = (id) => {
    //here also notification one apply
   // const whofollowed = this.props.usr_id;
   // fetch('/followed/' + id + '/' + whofollowed)
   // .then(res => console.log(res))
   // .catch(err => console.log(err));
  }

  handlePost = (id) => {
    fetch('/userposts/' + id)
    .then(res => res.json())
    .then(data => this.setState({userposts:data}));
  }

  render() {
   
  const user=this.state.user;
  
  // const posts = (this.state.userposts && this.state.userposts.map(post => {
  //                   return (
  //                     <p>Post here</p>
  //                     );
  //               })
  //               );
  
  //see posts one

  //<p>Following :{user[i].following}  </p>//an array itself
  //<p>Followers :{user[i].followers}</p>//an array itself

  for(var i=0;i<user.length;i++)
  {
    if(user[i]._id===this.props.match.params.id)
    {
      //console.log(user[i].following);
       var mainuser=(
        <div>
        <h4>Name :{user[i].name}</h4>
        <p>Email : {user[i].email}</p>
        <p>Joined On : {user[i].joindate}</p>
        <button className='btn btn-primary' onClick={() => this.handlePost(this.props.match.params.id)}>Get Posts</button>
        
        <button onClick={() => this.handleClick(user[i]._id)} > Follow </button>
     </div>);

    }
  }
  
    return (

       <div>

        <div className="card text-center">
          <div className="card-header">
            <ul className="nav nav-tabs card-header-tabs">
              <li className="nav-item">
                <button className='btn btn-primary' onClick={() => this.handlePost(this.props.match.params.id)}>
                    Get Posts
                </button>
              </li>
              <li className="nav-item">
                <button className='nav-link' onClick={() => this.handleFollowing(this.props.match.params.id)}>Following</button>
              </li>
               <li className="nav-item">
                <button className='nav-link' onClick={() => this.handleFollowers(this.props.match.params.id)}>Followers</button>
               </li>
               <li className="nav-item">
                <button className='nav-link' onClick={() => this.handlePosts(this.props.match.params.id)}>Posts</button>
               </li>
            </ul>
          </div>
          <div className="card-body">
              {mainuser}
          </div>
      </div>
    </div>
    
    );
  }
}

export default Userprofile;
