import React, { Component } from 'react';

class Followedpost extends Component {
  
  constructor(){
    super();
    this.state={
      user:''
    }
  }
  

  componentDidMount(){

    fetch('/user')
    .then(res => res.json())
    .then(data => this.setState({user:data}));
    
  }
  render() { 
    const user=this.state.user;

    return (
    <div>


     { user && user.map(user => {
        return (
          <div>
          	 <h2>Name</h2>
	        <p>Caption and Image</p>
	        <p>No of comments</p>
	        <p>Comments</p>
	        <button>Like</button>
	        <form>
	        <input type="text" placeholder="Comment here" name="cmt" />
	        </form>
          </div>

      );
      })}
      </div>
  );
      }}


export default Followedpost;
