import React, { Component } from 'react';
import Follow from './follow';
import Followedpost from './followedpost';
import FeedNav from './feednav';
class Newsfeed extends Component {

  constructor(){
    super();
    this.state={
      me:'',
      post:''
    }
  }

   componentDidMount(){

    this.setState({me:this.props.location.state.usr});
  }

  handleSubmit = (e , id) => {

    e.preventDefault();
    fetch('/addpost/' + id);

  }
  

  render() {
    if(this.state.me){
    const owner = this.state.me;
    }
    return (
      <div>
      <FeedNav id={this.state.me._id} />
      <div className="container">
        <div className='row'>
          <div className="col-7">
            <Link to={'/myprofile' + this.state.me._id}>My Profile</Link>
            <div className='outerbox'>
            <p style={{color:'#009688',marginLeft:10,marginTop:-1,fontWeight:'bold',fontSize:19}}>Newsfeed</p>
            <div className='innerbox'>
            <div className='innermost'>
            <p>{owner.name}</p>
            <form onSubmit={() => this.handleSubmit(owner._id)} className='white'>
              <div className='input-field'>
                <label htmlFor='title'>Post here</label>
                <input type='text' id='title' name='title' onChange={this.handleChange} />
              </div>
              <div className='input-field'>
                <button className='btn pink lighten-1 z-dpth-0'>POST</button>
              </div>
            </form>
            </div>
            </div>
            </div>

            <div style={{marginTop:30}} >
            <Followedpost usr_id= {this.state.me._id} />
            </div>

          </div>

          <div className='col-5'>
            <Follow usr_id= {this.state.me._id}/>
          </div>

        </div>
     </div>
     </div>
    );
  }
}

export default Newsfeed;
