import React, { Component } from 'react';

class Followedpost extends Component {
  
  constructor(){
    super();
    this.state={
      user:''
    }
  }
  

  componentDidMount(){

    fetch('/user')
    .then(res => res.json())
    .then(data => this.setState({user:data}));
    
  }

  render() { 
    const user=this.state.user;

    

    return (
    <div>
      { user && user.map(user => {
                {user.posts && user.posts.map(eachpost => {
                  return (
                    <div>
                      <h2>Name : {user.name }</h2>
                      <p>Caption and Image : {eachpost.caption}</p>
                      <p>No of comments : {eachpost.comment.length}</p>
                      {eachpost.comment && eachpost.comment.map(eachcomment => {
                      <p>Comments : {eachcomment} </p>
                      })}
                      <button>Like : {eachpost.likes}</button>
                      <form>
                      <input type="text" placeholder="Comment here" name="cmt" />
                      </form>
                    </div>

      );
      })}
      })};
    </div>
  );

}}

export default Followedpost;
