import React, { Component } from 'react';
import {Link} from 'react-router-dom';

class MyProfile extends Component {

  constructor(){
    super();
    this.state={
      user:'',
      grp:'',
      grpinfo:'',
      flag:'normal',
      following:'',
      followers:'',
      tag:'',
      share:'',
      msgs:'',
      post:''
    }
  }

  componentWillMount(){

    fetch('/user/' + this.props.match.params.id)
    .then(res => res.json())
    .then(data => this.setState({user:data}));
    
  }

  handleGrp1 = (e) => {
    this.setState({grp:e.target.value});
  }

  handleGrp2 = (e,usrid) => {
    e.preventDefault();
    fetch('/getgrp/' + usrid + '/' + this.state.grp)
    .then(res => res.json())
    .then(data => this.setState({grpinfo:data}));
  }

  handleFollowing = (id) => {
    fetch('/myfollowing/' + id)
    .then(res => res.json())
    .then(data => this.setState({following:data,flag:'following'}));
  }

  handleFollowers = (id) => {
    fetch('/myfollowers/' + id)
    .then(res => res.json())
    .then(data => this.setState({followers:data,flag:'followers'}));
  }

   handlePosts = (id) => {
    fetch('/myposts/' + id)
    .then(res => res.json())
    .then(data => this.setState({post:data,flag:'posts'}));
  }
  
  handleTag = (id) => {
    fetch('/mytag/' + id)
     .then(res => res.json())
    .then(data => this.setState({tag:data,flag:'tag'}));
  }

  handleShare = (id) => {
    fetch('/myshare/' + id)
     .then(res => res.json())
    .then(data => this.setState({share:data,flag:'share'}));
  }

  handleGroups = (id) => {
    this.setState({flag:'groups'});
  }

  handleMessages = (id) => {
    fetch('/mymessages/' + id)
    .then(res => res.json())
    .then(data => this.setState({msgs:data,flag:'msgs'}));
  }

  render() {


    if(this.state.user){
       var user=this.state.user[0];
    }


    // var grps = (user.groups && user.groups.map(grp => {
    //       return (
    //         <input type="radio" name="grpname" value={grp._id} onChange = {this.handleGrp1} > {grp.name}<br>
    //       );
    //     })
    //   )

     var flag=this.state.flag;

      if(this.state.flag=='normal' && user){
         var state =  (
          <div>
            <h5 className="card-title">Name :{user.name} - {user.initials}</h5>
            <p class="card-text">Email : {user.email} , Joined On : {user.joindate}</p>
          </div>
        );
        }

   

   


    return (
     <div>

        <div className="card text-center">
          <div className="card-header">
            <ul className="nav nav-tabs card-header-tabs">
              <li className="nav-item">
                <a className="nav-link active" href="#">Posts</a>
              </li>
              <li className="nav-item">
                <button className='nav-link' onClick={() => this.handleFollowing(this.props.match.params.id)}>Following</button>
              </li>
               <li className="nav-item">
                <button className='nav-link' onClick={() => this.handleFollowers(this.props.match.params.id)}>Followers</button>
               </li>
               <li className="nav-item">
                <button className='nav-link' onClick={() => this.handlePosts(this.props.match.params.id)}>Posts</button>
               </li>
               <li className="nav-item">
                <button className='nav-link' onClick={() => this.handleTag(this.props.match.params.id)}>Tag</button>
               </li>
               <li className="nav-item">
                <button className='nav-link' onClick={() => this.handleShare(this.props.match.params.id)}>Share</button>
               </li>
               <li className="nav-item">
                <button className='nav-link' onClick={() => this.handleGroups(this.props.match.params.id)}>Groups</button>
               </li>
               <li className="nav-item">
                <button className='nav-link' onClick={() => this.handleMessages(this.props.match.params.id)}>Messages</button>
               </li>
            </ul>
          </div>
          <div className="card-body">
          {state}
          </div>
      </div>

     </div>

    );
 
}}

export default MyProfile;
