import React, { Component } from 'react';
import {Link} from 'react-router-dom';

class GrpAbout extends Component {

   constructor(){
    super();
    this.state={
      grpinfo:''
    }
  }

  componentWillMount = () => {
    const id = this.props.match.params.id;
    fetch('/grpabout/' + id)
    .then(res => res.json())
    .then(data => this.setState({grpinfo:data}));
  }

  handleRemove = (memid , grpid) => {
    fetch('/removeGrpMember/' + memid + '/' + grpid)
    .then(res => res.json())
    .then(data => {
      fetch('/grpabout/' + grpid)
      .then(res => res.json())
      .then(data => this.setState({grpinfo:data}));
    })
  }

  render() {
    
    var grp = this.state.grpinfo;
    if(grp.members.length!=0){
      var members = (grp.members && grp.members.map(member => {
                    return (
                         <div className="col-sm-4">
                          <div className="card">
                            <div className="card-body">
                              <h5 className="card-title">{member}</h5>
                              <Link to={'/userprofile/' + member._id}><button class="btn btn-primary">Profile</button></Link>
                              <button onClick={() => this.handleRemove(member_id , this.props.match.params.id)} 
                              class="btn btn-primary">Remove</button>
                            </div>
                          </div>
                        </div>
                    );
      })
      );
    } else {
      var members = "No members in this group to show..";
    }
    return (

      <div>
       <div className="container">
          <div className="row">
          {members}
          </div>
      </div>
      </div>

    );
  }
}

export default GrpGallery;
