import React, { Component } from 'react';

class Follow extends Component {

  handleClick(){
      this.props.val(true);
   }
   
  constructor(){
    super();
    this.state={
      users:{}
    }
  }

  componentDidMount(){

    fetch('/user')
    .then(res => res.json())
    .then(data => this.setState({users:data[0]}));
  }

  render() {
    

    return (
   
     <div>
        <h2>{this.state.users.name}</h2>
        <button onClick={this.handleClick.bind(this)}>Follow</button>

     </div>
     
    );
  }
}

export default Follow;
