import React, { Component } from 'react';

class Followedpost extends Component {
  
  constructor(props){
    super(props);
    this.state={
      user:'',
      share_seleted : '',
      tag_selected : '',
      following : '',
      posts : '',
      cmt:'',
      allusers : '',
      publicKey:'BKgPEG-yxGjEwubcob9tfvgfOkqjJW9EPe60SDiaJNo_EudTYOMZGkjdVP9wzVUJOCcmecelw4OKbHu-tcw2p4Y'
    }
  }

  componentWillMount(){
   var id = this.props.usr_id;
    console.log(id)
    fetch('/followedpost/' + id)
    .then(res => res.json())
    .then(data => this.setState({posts:data}));

    fetch('/myfollowing/' + id)
    .then(res => res.json())
    .then(data => this.setState({user:data}));
  }

  handleChange = (e) => {
    this.setState({[e.target.name]:e.target.value});
  }
  
  // handlePost = (name) => {
  //   fetch('/showpost/' + name)
  //     .then(res => res.json())
  //     .then(data => this.setState({posts : data.posts}))
  // }

  handleSubmit = (e ,id) => {
    e.preventDefault();
    var cmt = this.state.cmt;
    fetch('/comment/' + cmt + '/' + id)
      .then(res => res.json())
      .then(data => this.setState({posts : data.posts}));
  }
 
  handleLike = (id) => {

    fetch('/post/' + id)
      .then(res => res.json())
      .then(data => this.setState({posts : data.posts}))
  }

  handleShare1 = (e) => {
    
    var shared = this.state.share_seleted;
    shared.push(e.target.value);
    this.setState({share_seleted : shared});
  }

  handleShare2 = async (e , id , myid) => {
    e.preventDefault();

    //store this somewhere??

    if('serviceWorker' in navigator){
      console.log('Registering SW...');
      const register = await navigator.serviceWorker.register('./worker2.js',{
        scope:'/'
      });

        console.log('SW is registered...');
        console.log('Register Subscription...');
        const subscription=register.pushManager.subscribe({
        userVisibleOnly:true,
        applicationServerKey:this.urlBase64ToUint8Array(this.state.publicKey)
        });
      
        console.log('Push notification...');
        fetch('/share/' + id + '/' + myid + '/' + this.state.share_seleted,{
        method:'POST',
        body:JSON.stringify(subscription),
        headers:{
          'content-type':'application/json'
        }
      });
    }
  }

   handleTag1 = (e) => {
    
    var taged = this.state.tag_seleted;
    taged.push(e.target.value);
    this.setState({tag_seleted : taged});
  }

  handleTag2 = async (e , id , myid) => {
    e.preventDefault();
    if('serviceWorker' in navigator){
      console.log('Registering SW...');
      const register = await navigator.serviceWorker.register('./worker2.js',{
        scope:'/'
      });

        console.log('SW is registered...');
        console.log('Register Subscription...');
        const subscription=register.pushManager.subscribe({
        userVisibleOnly:true,
        applicationServerKey:this.urlBase64ToUint8Array(this.state.publicKey)
        });
      
        console.log('Push notification...');
        fetch('/tag/' + id + '/' + myid + '/' + this.state.tag_seleted,{
        method:'POST',
        body:JSON.stringify(subscription),
        headers:{
          'content-type':'application/json'
        }
      });
    }
  }


  urlBase64ToUint8Array = (base64String) => {
    const padding = '='.repeat((4 - base64String.length % 4) % 4);
    const base64 = (base64String + padding)
      .replace(/-/g, '+')
      .replace(/_/g, '/');

    const rawData = window.atob(base64);
    const outputArray = new Uint8Array(rawData.length);

    for (let i = 0; i < rawData.length; ++i) {
      outputArray[i] = rawData.charCodeAt(i);
    }
    return outputArray;
  }

  render() { 
    // var compo = (this.state.following && this.state.following.map(name => {
    //               return (
    //                 <div><button onClick={() => this.handlePost(name)}>{name}</button></div>
    //               );
    //             })
    //             )

    var share = (this.state.user && this.state.user.map(user => {
                  return (
                    <div>
                    <input type="checkbox" name="name" value={user._id} onChange = {this.handleShare1} /> {user.name} - Share <br/>
                    </div>
                  );
                })
                )

    var tag = (this.state.user && this.state.user.map(user => {
                  return (
                    <div>
                    <input type="checkbox" name="name" value={user._id} onChange = {this.handleTag1} /> {user.name} - Tag <br />
                    </div>
                  );
                })
                )

    var posts =  (this.state.posts && this.state.posts.map(post => {
                  return (

                    <div className="card" style="width: 20rem;">
                    <img className="card-img-top" src={post.pic} alt="Card image cap" />
                    <div className="card-body">
                      <h5 className="card-title">{post.name} ,  {post.date}</h5>
                      <p class="card-text">Caption : {post.caption}</p>
                      <form onSubmit = {() => {this.handleShare2(post._id , this.props.usr_id)}} >
                    {share}
                    </form>
                    <form onSubmit = {() => {this.handleTag2(post._id , this.props.usr_id)}} >
                    {tag}
                    </form>
                    <form onSubmit={() => {this.handleSubmit(post._id)}} >
                    // post p comment hone p post owner ko msg bhejna hoga
                    <div className='form-group'>
                      <label for='cmt'>Comment Here</label>
                      <input type='text' className='form-control' name='cmt'  onChange = {this.handleChange} />
                    </div>
                    <button className='btn btn-success'>Comment</button>
                    </form>
                    </div>
                     <div className="card-footer text-muted">
                        <p>Likes : {post.likes}</p>
                        <p>Comment : {post.comment}</p> //add scroll bar for comment section to read //also array so map 
                    </div>
                  </div>

        
                  );
                })
    );

  return (
  <div>
  {posts}
  </div>
  );

}}

export default Followedpost;
