import React, { Component } from 'react';
import Userprofile from './userprofile';

class Follow extends Component {

   constructor(){
    super();
    this.state={
      user:'',
      followed:true
    }
  }
  
  handleClick(value){
    this.setState({followed:value});
  }

  componentDidMount(){

    fetch('/user')
    .then(res => res.json())
    .then(data => this.setState({user:data[0]}));
    
  }
  

  render() { 
    console.log(this.state);
    const profile=(this.state.followed) ? (<div><Userprofile user={this.state} /></div>):null ;

    return (
     <div>
        <h2>{this.state.user.name}</h2>
        <button onClick={this.handleClick.bind(this)} >Follow</button>
        {profile}
     </div>
     
    );
  }
}

export default Follow;
