import React, { Component } from 'react';
import Userprofile from './userprofile';

class Follow extends Component {

   constructor(){
    super();
    this.state={
      email:'',
      name:'',
      followers:'',
      following:'',
      joindate:'',
      posts:[],
      followed:true
    }
  }
  
  handleClick(value){
    this.setState({followed:value});
  }

  componentDidMount(){

    fetch('/user')
    .then(data => this.setState({email:data[0].email,name:data[0].name,followers:data[0].followers,
      following:data[0].following,joindate:data[0].joindate,posts:data[0].posts}));
    
  }
  

  render() { 
  
    const profile=(this.state.followed) ? (<div><Userprofile user={this.state.email} /></div>):null ;
    var Obj=this.state;

    var arr2 = Object.keys(Obj).map(function (i) {
      console.log( Obj[i]);
    });

    return (
     <div>
        <h2>{this.state.name}</h2>
        <button onClick={this.handleClick.bind(this)} >Follow</button>
        {profile}
     </div>
     
    );
  }
}

export default Follow;
