import React, { Component } from 'react';
import {NavLink} from 'react-router-dom';

class Userprofile extends Component {

  constructor(){
    super();
    this.state={
      user:'',
      userposts:'',
      following:'',
      flag:'normal',
      followers:''
    }
  }

  componentWillMount(){

    fetch('/user/' + this.props.match.params.id)
    .then(res => res.json())
    .then(data => this.setState({user:data}));
    
  }

  // handlePost = (id) => {
  //   fetch('/userposts/' + id)
  //   .then(res => res.json())
  //   .then(data => this.setState({userposts:data}));
  // }

   handleFollowing = (id) => {
    fetch('/myfollowing/' + id)
    .then(res => res.json())
    .then(data => this.setState({following:data,flag:'following'}));
  }

  handleFollowers = (id) => {
    fetch('/myfollowers/' + id)
    .then(res => res.json())
    .then(data => this.setState({followers:data,flag:'followers'}));
  }

  render() {

   if(this.state.user)
       var user=this.state.user[0];

  
  // const posts = (this.state.userposts && this.state.userposts.map(post => {
  //                   return (
  //                     <p>Post here</p>
  //                     );
  //               })
  //               );
  
  //see posts one

  //<p>Following :{user[i].following}  </p>//an array itself
  //<p>Followers :{user[i].followers}</p>//an array itself

   if(this.state.flag==='followers' && user){
      var  state = (
        <div>
      <ul className="collection">
      {this.state.followers && this.state.followers.map(usr => {
      if(usr._id !== user._id){
        return (
            <li className="collection-item avatar">
              <img src={usr.img} alt="" className="circle" />
              <span className="title">{usr.name}</span>
              <a href="#!" class="secondary-content"><i class="material-icons">grade</i></a>
              <NavLink to={'/userprofile/' + usr._id}><button class="btn btn-outline-warning">View Profile</button></NavLink>
              <button class="btn btn-outline-success" onClick={() => this.handleClick(usr._id)} > Follow </button>
            </li>
          ); }})}
      </ul>
      </div>
        );
    }

     else if(this.state.flag==='following' && user){
      var  state = (
        <div>
      <ul className="collection">
      {this.state.following && this.state.following.map(usr => {
      if(usr._id !== user._id){
        return (
            <li className="collection-item avatar">
              <img src={usr.img} alt="" className="circle" />
              <span className="title">{usr.name}</span>
              <a href="#!" class="secondary-content"><i class="material-icons">grade</i></a>
              <NavLink to={'/userprofile/' + usr._id}><button class="btn btn-outline-warning">View Profile</button></NavLink>
              <button class="btn btn-outline-success" onClick={() => this.handleClick(usr._id)} > Follow </button>
            </li>
          ); }})}
      </ul>
      </div>
        );
    }



  for(var i=0;i<user.length;i++)
  {
    if(user[i]._id===this.props.match.params.id)
    {
      //console.log(user[i].following);
      console.log('kya yha')
       var mainuser=(
        <div>

          <div>
          <h4>Name :{user[i].name}</h4>
          <p>Email : {user[i].email}</p>
          <p>Joined On : {user[i].joindate}</p>
          </div>

          <div className="card text-center">
          <div className="card-header">
            <ul className="nav nav-tabs card-header-tabs">
              <li className="nav-item">
                <button className='btn btn-primary' onClick={() => this.handlePost(this.props.match.params.id)}>
                  Posts
                </button>
              </li>
              <li className="nav-item">
                <button className='nav-link' onClick={() => this.handleFollowing(this.props.match.params.id)}>Following</button>
              </li>
               <li className="nav-item">
                <button className='nav-link' onClick={() => this.handleFollowers(this.props.match.params.id)}>Followers</button>
               </li>  
            </ul>
          </div>
          <div className="card-body">
              {state}
          </div>
      </div>
    </div>

    );

    }
  }
  
    return (

       <div>
       {mainuser}
       </div>
        
    
    );
  }
}

export default Userprofile;
