import React, { Component } from 'react';
import {NavLink , Link} from 'react-router-dom';

class MyProfile extends Component {

  constructor(){
    super();
    this.state={
      user:'',
      grp:'',
      grpinfo:'',
      flag:'normal',
      following:'',
      followers:'',
      tag:'',
      share:'',
      msgs:'',
      post:''
    }
  }

  componentWillMount(){

    fetch('/user/' + this.props.match.params.id)
    .then(res => res.json())
    .then(data => this.setState({user:data}));
    
  }

  handleGrp1 = (e) => {
    this.setState({grp:e.target.value});
  }

  handleGrp2 = (e,usrid) => {
    e.preventDefault();
    fetch('/getgrp/' + usrid + '/' + this.state.grp)
    .then(res => res.json())
    .then(data => this.setState({grpinfo:data}));
  }

  handleFollowing = (id) => {
    fetch('/myfollowing/' + id)
    .then(res => res.json())
    .then(data => this.setState({following:data,flag:'following'}));
  }

  handleFollowers = (id) => {
    fetch('/myfollowers/' + id)
    .then(res => res.json())
    .then(data => this.setState({followers:data,flag:'followers'}));
  }

   handlePosts = (id) => {
    fetch('/myposts/' + id)
    .then(res => res.json())
    .then(data => this.setState({post:data,flag:'posts'}));
  }
  
  handleTag = (id) => {
    fetch('/mytag/' + id)
     .then(res => res.json())
    .then(data => this.setState({tag:data,flag:'tag'}));
  }

  handleShare = (id) => {
    fetch('/myshare/' + id)
     .then(res => res.json())
    .then(data => this.setState({share:data,flag:'share'}));
  }

  handleGroups = (id) => {
    this.setState({flag:'groups'});
  }

  handleMessages = (id) => {
    fetch('/mymessages/' + id)
    .then(res => res.json())
    .then(data => this.setState({msgs:data,flag:'msgs'}));
  }

  render() {


    if(this.state.user)
       var user=this.state.user[0];

    if(user){
    var grps = (user.groups && user.groups.map(grp => {
          return (
            <div>
            <input type="radio" name="grpname" value={grp._id} onChange = {this.handleGrp1} /> {grp.name} 
            </div>
          );
        })
      )
    }

     var flag=this.state.flag;

     if(this.state.flag=='normal' && user){
         state =  (
          <div>
            <h5 className="card-title">Name :{user.name} - {user.initials}</h5>
            <p className="card-text">Email : {user.email} , Joined On : {user.joindate}</p>
          </div>
        )
        }

    else if(this.state.flag=='followers' && user){
      var  state = (
        <div>
      <ul className="collection">
      {this.state.followers && this.state.followers.map(usr => {
      if(usr._id !== user._id){
        return (
            <li className="collection-item avatar">
              <img src={usr.img} alt="" className="circle" />
              <span className="title">{usr.name}</span>
              <a href="#!" class="secondary-content"><i class="material-icons">grade</i></a>
              <NavLink to={'/userprofile/' + usr._id}><button class="btn btn-outline-warning">View Profile</button></NavLink>
              <button class="btn btn-outline-success" onClick={() => this.handleClick(usr._id)} > Follow </button>
            </li>
          ); }})}
      </ul>
      </div>
        );
    }

    else if(this.state.flag=='posts' && user){
      state=(this.state.post && this.state.post.map(post => {
              return (

                  <div className="card" style="width: 20rem;">
                    <img className="card-img-top" src={post.pic} alt="Card image cap" />
                    <div className="card-body">
                      <h5 className="card-title">{post.name} ,  {post.date}</h5>
                      <p class="card-text">Caption : {post.caption}</p>
                    </div>
                  <div className="card-footer text-muted">
                      <p>Likes : {post.likes}</p>
                      <p>Comment : {post.comment}</p>
                  </div>
                </div>
      ) }))
    }

     else if(this.state.flag=='tag' && user){
       var state=(this.state.tag && this.state.tag.map(post => {
              return (

                  <div className="card" style="width: 20rem;">
                    <img className="card-img-top" src={post.pic} alt="Card image cap" />
                    <div className="card-body">
                      <h5 className="card-title">{post.name} ,  {post.date}</h5>
                      <p class="card-text">Caption : {post.caption}</p>
                    </div>
                  <div className="card-footer text-muted">
                      <p>Likes : {post.likes}</p>
                      <p>Comment : {post.comment}</p>
                  </div>
                </div>
      ) }))
    }

    else if(this.state.flag=='share' && user){
     var state=(this.state.share && this.state.share.map(post => {
              return (

                  <div className="card" style="width: 20rem;">
                    <img className="card-img-top" src={post.pic} alt="Card image cap" />
                    <div className="card-body">
                      <h5 className="card-title">{post.name} ,  {post.date}</h5>
                      <p class="card-text">Caption : {post.caption}</p>
                    </div>
                  <div className="card-footer text-muted">
                      <p>Likes : {post.likes}</p>
                      <p>Comment : {post.comment}</p>
                  </div>
                </div>
      ) }))
    }
   
    else if(this.state.flag=='groups' && user){

     var state = (<div><form onSubmit = {() => {this.handleGrp2(user._id)}} >
                     {grps}
                      <input type='submit' value='Search' />
                  </form>
                  <p><Link to={'/creategrp/' + user._id}><button>Create Group</button></Link></p>
                  </div>
                )
    }  

    else if(this.state.flag=='msgs' && user){
      state = (this.state.msgs && this.state.msgs.map(msg => {
        return (
          <div className="card">
            <div className="card-header">
              {msg}
            </div>
          </div>
    ) }) )
    }



    if(this.state.grpinfo){
      return (
       <Redirect to={{pathname:'/grps' , state:{grpinfo:this.state.grpinfo}}} />
      )
    }

 
      return (
     <div>

        <div className="card text-center">
          <div className="card-header">
            <ul className="nav nav-tabs card-header-tabs">
              <li className="nav-item">
                <a className="nav-link active" href="#">Posts</a>
              </li>
              <li className="nav-item">
                <button className='nav-link' onClick={() => this.handleFollowing(this.props.match.params.id)}>Following</button>
              </li>
               <li className="nav-item">
                <button className='nav-link' onClick={() => this.handleFollowers(this.props.match.params.id)}>Followers</button>
               </li>
               <li className="nav-item">
                <button className='nav-link' onClick={() => this.handlePosts(this.props.match.params.id)}>Posts</button>
               </li>
               <li className="nav-item">
                <button className='nav-link' onClick={() => this.handleTag(this.props.match.params.id)}>Tag</button>
               </li>
               <li className="nav-item">
                <button className='nav-link' onClick={() => this.handleShare(this.props.match.params.id)}>Share</button>
               </li>
               <li className="nav-item">
                <button className='nav-link' onClick={() => this.handleGroups(this.props.match.params.id)}>Groups</button>
               </li>
               <li className="nav-item">
                <button className='nav-link' onClick={() => this.handleMessages(this.props.match.params.id)}>Messages</button>
               </li>
            </ul>
          </div>
          <div className="card-body">
          {state}
          </div>
      </div>
    </div>
     

    );
 
}}

export default MyProfile;
